// For n8n Code Node
// This matches automations with charges based on product name
try {
  // Get all input items
  const items = $input.all();

  // Initialize arrays
  let automationsList = [];
  let chargesList = [];

  // Separate automations and charges from all items
  items.forEach(item => {
    const data = item.json;

    // Check if it's an automation (has output with Name)
    if (data.output && data.output.Name) {
      automationsList.push(data.output);
    }
    // Check if it's a charge (has customer_name and payment_intent)
    else if (data.customer_name && data.payment_intent) {
      chargesList.push(data);
    }
  });

  // Filter successful charges only
  const successfulCharges = chargesList.filter(charge => {
    return charge && charge.status === "succeeded";
  });

  // Match automations with charges based on product name
  const matchedResults = [];

  successfulCharges.forEach(charge => {
    // Find matching automation by comparing names
    // Remove [n8n] suffix and trim for comparison
    const chargeProdName = (charge.product_name || "").replace(/\s*\[n8n\]\s*$/i, "").trim();

    const matchingAutomation = automationsList.find(automation => {
      const autoName = (automation.Name || "").replace(/\s*\[n8n\]\s*$/i, "").trim();
      return autoName === chargeProdName;
    });

    if (matchingAutomation) {
      matchedResults.push({
        // Automation Info
        automationName: matchingAutomation.Name || "Unknown",
        automationRow: matchingAutomation.row_number || "",
        googleDriveLink: matchingAutomation["Google Drive Link"] || "",
        password: matchingAutomation.Password || "",

        // Customer Info
        customerName: charge.customer_name || "Unknown",
        customerEmail: charge.email || "",

        // Payment Info
        amount: charge.amount ? (charge.amount / 100).toFixed(2) : "0.00",
        currency: (charge.currency || "usd").toUpperCase(),
        paymentIntent: charge.payment_intent || "",
        orderReference: charge.order_reference || "",
        productName: charge.product_name || "",
        purchaseDate: charge.created ? 
          new Date(charge.created * 1000).toLocaleDateString() : "",
        status: charge.status || "unknown",
        receiptUrl: charge.receipt_url || ""
      });
    }
  });

  // Return results
  if (matchedResults.length > 0) {
    return matchedResults;
  } else {
    return [{
      error: "No matching data found",
      automations_found: automationsList.length,
      charges_found: successfulCharges.length,
      automation_names: automationsList.map(a => a.Name),
      charge_product_names: successfulCharges.map(c => c.product_name)
    }];
  }

} catch (error) {
  return [{
    error: error.message,
    stack: error.stack
  }];
}

// Get all combined items
const all = $input.all().map(i => i.json);

// Separate by type
const charges = all.filter(i => i.object === 'charge');
const intents = all.filter(i => i.object === 'payment_intent');
const products = all.filter(i => i.object === 'product');

// Merge logic
const merged = [];

for (const charge of charges) {
  const intent = intents.find(i => i.id === charge.payment_intent);
  if (!intent) continue;

  const orderRef = intent?.payment_details?.order_reference;
  const product = products.find(p => p.id === orderRef);

  merged.push({
    customer_name: charge.billing_details?.name || null,
    email: charge.billing_details?.email || intent.receipt_email || null,
    amount: charge.amount / 100,
    currency: charge.currency,
    status: charge.status,
    payment_intent: charge.payment_intent,
    order_reference: orderRef,
    product_name: product?.name || null,
    product_description: product?.description || null,
    product_image: product?.images?.[0] || null,
    receipt_url: charge.receipt_url,
    created: charge.created
  });
}

return merged.map(item => ({ json: item }));

