parameters.jsCode:
const input = $json;

return [{
  analysis_input: {
    query: input.search_parameters?.q || null,
    region: input.search_parameters?.uule || null,

    organic_insights: (input.organic_results || []).map(r => ({
      title: r.title || "",
      snippet: r.snippet || "",
      source: r.source || ""
    })),

    people_also_ask: (input.related_questions || []).map(q => q.question),

    related_searches: (input.related_searches || []).map(r => r.query),

    meta: {
      total_results: input.search_information?.total_results || 0,
      time_taken: input.search_information?.time_taken_displayed || null
    }
  }
}];

