// Loop over input items and add a new field called 'myNewField' to the JSON of each one
let article = '';
for (const item of $input.first().json.paragraphs) {
  article += item
}

return {content : article}

// Process GNews response and prepare for scraping
const articles = $input.first().json.articles;

if (!articles || !Array.isArray(articles)) {
    throw new Error('No articles found in response');
}

// Create array of URLs to scrape
const urlsToScrape = articles.map((article, index) => ({
    json: {
        id: index + 1,
        originalTitle: article.title,
        url: article.url,
        source: article.source?.name,
        gnewsContent: article.content // Keep the truncated content as fallback
    }
}));

return urlsToScrape;
