parameters.jsCode:
function replaceDashesWithCommas(str) {
  return str
    // turn hyphen/en dash/em dash/horizontal bar into commas
    .replace(/[\u2012\u2013\u2014\u2015]/g, ',')
    // collapse multiple commas
    .replace(/,+/g, ',')
    // normalize spaces around commas (preserve newlines)
    .replace(/[ \t]*,[ \t]*/g, ', ')
    // remove leading/trailing spaces/tabs only (keep any \n)
    .replace(/^[ \t]+|[ \t]+$/g, '')
    // drop a leading or trailing comma (with optional spaces/tabs)
    .replace(/^[ \t]*,|,[ \t]*$/g, '');
}

$input.first().json.message.content.content =
  replaceDashesWithCommas($input.first().json.message.content.content);

console.log($input.first().json.message.content.content);
$execution.customData.set("image_prompt",$input.first().json.message.content.prompt)
return $input.all();

// Loop over input items and add a new field called 'myNewField' to the JSON of each one
for (const item of $input.all()) {
  if(typeof(item.json.message) === 'object'){
    $input.first().json.headers.host = item.json.message.content.prompt
    $input.first().json.headers.accept = item.json.message.content.content

  }
}
// console.log(Object.keys($input.first().json.message))
return $input.first();

